package by.matthewvirus.jdbcwebapp.mysql.business;import java.io.Serializable;import java.util.Objects;public class User implements Serializable {    private static final long serialVersionUID = 1L;    private int id;    private String name;    private String surname;    private int age;    private String gender;    public User() {        this.id = 0;        this.name = "";        this.surname = "";        this.age = 0;        this.gender = "";    }    public User(String name, String surname, int age, String gender) {        this.name = name;        this.surname = surname;        this.age = age;        this.gender = gender;    }    public User(int id, String name, String surname, int age, String gender) {        this.id = id;        this.name = name;        this.surname = surname;        this.age = age;        this.gender = gender;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getSurname() {        return surname;    }    public void setSurname(String surname) {        this.surname = surname;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }    public String getGender() {        return gender;    }    public void setGender(String gender) {        this.gender = gender;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        User user = (User) o;        return age == user.age &&                Objects.equals(name, user.name) &&                Objects.equals(surname, user.surname) &&                Objects.equals(gender, user.gender);    }    @Override    public int hashCode() {        return Objects.hash(name, surname, age, gender);    }    @Override    public String toString() {        return "User{" +                "name='" + name + '\'' +                ", surname='" + surname + '\'' +                ", age=" + age +                ", gender='" + gender + '\'' +                '}';    }}